mapper.select:
SELECT grade
FROM MyCircle_tb
WHERE user_id = #{user_id} AND circle_name = #{circle_name}

SELECT user_id,grade
FROM MyCircle_tb
WHERE circle_name=#{circle_name}

SELECT circle_name
FROM MyCircle_tb
WHERE user_id=#{user_id}

mapper.update:
UPDATE MyCircle_tb
SET grade = 2
WHERE user_id=#{user_id}
 AND circle_name = #{circle_name}
